import React, {Component} from "react"
import {Redirect, Link} from "react-router-dom"
import Form from "react-bootstrap/Form"

import axios from "axios"

import {SERVER_HOST} from "../config/global_constants"

import LinkInClass from "../components/LinkInClass"


export default class SignInCompany extends Component 
{
    constructor(props) 
    {
        super(props)
        
        this.state = {
            username:"",
            isValidUsername: false,
            name:"",
            isValidName: false,
            phone_number:null,
            isValidphone_number: false,
            selectedFile:null, 
            isValidImage: false,
            password:"",
            isValidPassword: false,
            passwordConfirmation:"",
            submited:false
        }
    }


    componentDidMount() 
    {     
        this.inputToFocus.focus()        
    }

    handleChange = (e) => 
    {
        this.setState({[e.target.name]: e.target.value})

    }

    validateConfirmPassword()
    {    
        return (this.state.password === this.state.passwordConfirmation); 
    }

    validateName()
    {    
        if(this.state.name.length>0){
            return true;
        }
        return false;
    }


    validateUsername()
    {    
        if(this.state.username.length>3){
            return true;
        }
        return false;
    }


    validatePassword(){
        if(this.state.password.length>5){
            return true;
        }
        return false;
    }

    validateImage(){
        if(this.state.selectedFile!=null){
            return true;
        }
        return false;
    }

    validate(){
        this.state.isValidUsername = this.validateUsername();
        this.state.isValidName = this.validateName();
        this.state.isValidphone_number = this.validatephone_number();
        this.state.isValidPassword = this.validatePassword();
        this.state.isValidImage = this.validateImage();
    }

    isAllValid(){
        return this.state.isValidUsername && this.state.isValidName && this.state.isValidphone_number && this.state.isValidPassword && this.validateConfirmPassword();
    }


    handleSubmit = (e) =>
    {
        e.preventDefault();
        this.state.submited = true;
        this.validate();

        if(this.isAllValid()){

            const userObject = {
                name: this.state.name,
                username: this.state.username,
                phone_number: this.state.phone_number,
                password: this.state.password,
                img: null
            }

            let formData = new FormData()  
            formData.append("profilePhoto", this.state.selectedFile)

            userObject.img = formData;

            axios.post(`${SERVER_HOST}/companies`, userObject)
            .then(res => 
            {   
                if(res.data)
                {
                    if (res.data.errorMessage)
                    {
                        console.log(res.data.errorMessage)    
                    }
                    else
                    {   
                        console.log("Record added")
                        this.setState({redirectToDisplayAllCars:true})
                    } 
                }
                else
                {
                    console.log("Record not added")
                }
            }) 
        }

        

    }

    handleFileChange = (e) => 
    {
        this.setState({selectedFile: e.target.files[0]})
    }

