    {        
        return (
            <div>
                <img className="img-logo" src="logo.png" alt=""/>
                <div className="form-container">  
                    <Form>
                        <Form.Group controlId="username">
                            <Form.Label>Username</Form.Label>
                            <Form.Control ref = {(input) => { this.inputToFocus = input }} type="text" name="userName" value={this.state.userName} onChange={handle} />
                        </Form.Group>
            
                        <Form.Group controlId="password">
                            <Form.Label>Password</Form.Label>
                            <Form.Control ref = {(input) => { this.inputToFocus = input }} type="password" name="password"  />
                        </Form.Group>
                            
                        <Link className="green-button" to={"/MainPage"}>Log In</Link>       
                    
