import React, {Component} from "react"
import {Redirect, Link} from "react-router-dom"
import Form from "react-bootstrap/Form"

import axios from "axios"

import {SERVER_HOST} from "../config/global_constants"

import LinkInClass from "../components/LinkInClass"


export default class SignIn extends Component 
{
    constructor(props) 
    {
        super(props)
        
        this.state = {
            username:"",
            isValidUsername: false,
            name:"",
            isValidName: false,
            surname:"",
            isValidSurname: false,
            email:"",
            isValidEmail: false,
            password:"",
            isValidPassword: false,
            passwordConfirmation:"",
            submited:false
        }
    }


    componentDidMount() 
    {     
        this.inputToFocus.focus()        
    }

    handleChange = (e) => 
    {
        this.setState({[e.target.name]: e.target.value})

    }

    validateConfirmPassword()
    {    
        return (this.state.password === this.state.passwordConfirmation); 
    }

    validateName()
    {    
        if(this.state.name.length>0){
            return true;
        }
        return false;
    }

    validateSurname()
    {    
        if(this.state.surname.length>0){
            return true;
        }
        return false;
    }

    validateUsername()
    {    
        if(this.state.username.length>3){
            return true;
        }
        return false;
    }

     validateEmail()
    {    
        // valid email pattern
        const pattern = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        return pattern.test(String(this.state.email).toLowerCase());
    }

    validatePassword(){
        if(this.state.password.length>5){
            return true;
        }
        return false;
    }

    validate(){
        this.state.isValidUsername = this.validateUsername();
        this.state.isValidName = this.validateName();
        this.state.isValidSurname = this.validateSurname();
        this.state.isValidEmail = this.validateEmail();
        this.state.isValidPassword = this.validatePassword();
    }


    handleSubmit = (e) =>
    {
        this.state.submited = true;
        this.validate();
    }

